/// <reference path="../typings/node/node.d.ts"/>

// Node.js modules
var MapManager = require('./MapManager.js');
var GameModel  = require('./GameModel.js');
var blessed    = require('blessed');
var program    = blessed.program();
var async      = require('async');

// GameEngine object constructor
function GameEngine() {
  // Add a blessed screen object
  this.screen = blessed.screen({
    autoPadding: true,
    fastCSR: true
  });

  this.gameModel = new GameModel ();
  
  this.gameModel.on('game-over', function() {
    process.exit(0);
  });

  this.screen.title = 'sshokoban';
  
  this.gameBox = blessed.box({
    top: 'center',
    left: 'center',
    width: 80,
    height: 24,
    border: {
      type: 'line'
    },
    style: {
      fg: 'white',
      border: {
        fg: '#f0f0f0'
      },
    }
  });
}

// Initialize key bindings
GameEngine.prototype.initializeBindings = function () {
  var this_ge = this;
  
  // Add key events to the following:
  // -- Exit --
  this.screen.key(['escape', 'q', 'C-c'], function(ch, key) {
    return process.exit(0);
  });

  // -- Arrow keys --
  function process_arrow_key (ch, key) {
	var direction = key.name;
    if (this_ge.gameModel.canMovePlayer(direction)) {
      this_ge.gameModel.movePlayer(direction);
    }
    this_ge.render();
  };

  this.screen.key('left',  process_arrow_key);
  this.screen.key('right', process_arrow_key);
  this.screen.key('up',    process_arrow_key);
  this.screen.key('down',  process_arrow_key);
};


// Rendering
GameEngine.prototype.render = function () {
  this.screen.render();
  MapManager.render(this.gameModel, this.gameBox, program);
};

GameEngine.prototype.resetPlayerPosition = function () {
  for (var y = 0; y < this.gameModel.currentMap.height; y++) {
    for (var x = 0; x < this.gameModel.currentMap.width; x++) {
      if (this.gameModel.currentSessionState[y][x] == 'start') {
        this.gameModel.currentSessionState[y][x] = 'empty';
        //TODO: Dirty
        return {x: x, y: y};
      }
    }
  }     
}

// Begin the game (After construction)
GameEngine.prototype.run = function () {
  this.initializeBindings();
  this.screen.append(this.gameBox);
  
  var this_ge = this; // Clone object
  // Load the first level
  MapManager.load('level1.tmx', function(err, map) {
    MapManager.validate(map);
    this_ge.gameModel.currentMap = map;
    this_ge.gameModel.currentSessionState = MapManager.internalize(map);
    this_ge.gameModel.playerPosition = this_ge.resetPlayerPosition()
    this_ge.render();
  });
};

module.exports = GameEngine;
